import "./FiltroPesquisa.css"




const FiltroPesquisa = (props) => {

    const clickActive = (event) =>{
        console.log("Botao clicado foi", props.tab)
     


        if (props.tab == 1){
              event.currentTarget.classList.toggle('active');
        }


    }

    return (<div>
            <li ><a onClick={clickActive}  href={props.tab}>{props.label} </a></li>
        
    </div>);
}

export default FiltroPesquisa;
