import "./FiltroPesquisa.css"
import React from "react";

const handleClick = (event) => {
    const tab1 = document.querySelector("#tab1")
    const tab2 = document.querySelector("#tab2")
    const tab3 = document.querySelector("#tab3")

    if (event.target.id === "tab1") {
        event.target.parentNode.classList.add("active")
        tab2.parentNode.classList.remove("active")
        tab3.parentNode.classList.remove("active")
    } else if (event.target.id === "tab2") {
        event.target.parentNode.classList.add("active")
        tab1.parentNode.classList.remove("active")
        tab3.parentNode.classList.remove("active")
    } else {
        event.target.parentNode.classList.add("active")
        tab1.parentNode.classList.remove("active")
        tab2.parentNode.classList.remove("active")
    }
}


const FiltroPesquisa = (props) => {

    return (
        <div className="aba-busca">
            <ul className="aba-pequisa">
                <li className="aba"><button onClick={handleClick} id="tab1" >Buscar por produto</button></li>
                <li className="aba"><button onClick={handleClick} id="tab2">Palavra-chave</button></li>
                <li className="aba"><button onClick={handleClick} id="tab3" >Marca/modelo do ve√≠culo </button></li>
            </ul>
        </div>);
}

export default FiltroPesquisa;
