import { Input, Button, InputGroup } from "reactstrap";
import Api from "../Api/api";
import FiltroPesquisa from "../FiltroPesquisa/Index";
import "./Input.css"
import React,{useState} from "react";

const InputDefault = () => {

    const [produto, setProduto] = useState('');

    const handleChangeProduto = (e) =>{

        if (e.keyCode === 13)
        setProduto(e.target.value)
    }



return (
    <div className="grupo-pequisa">

        <FiltroPesquisa />

        <div className="container-input">
            <InputGroup>
                <Input placeholder="Encontre um produto digitado um código de peças Gauss ou um código referência"
                    value={produto} 
                    onChange={(e) => handleChangeProduto(e)}
                    />
                <Button onClick={Api(produto)}>
                    <img  src="./imagens/lupa.png" />
                </Button>
            </InputGroup>
        </div>
    </div>);
}

export default InputDefault;
