import "./FiltroPesquisa.css"




const FiltroPesquisa = (props) => {


    const tab = props.tab
    const [activeTab, setActiveTab] = useState({ tab });



    return (<div>
        <li className={activeTab === "tab1" ? "active" : ""} ><a href={props.tab}>{props.label} </a></li>

    </div>);
}

export default FiltroPesquisa;
